<?php

if ( ! defined( 'ABSPATH' ) ) {
	exit;
}

class Hostinger_Admin_Menu {
	public function __construct() {
		add_action( 'admin_menu', [ $this, 'admin_menu' ] );
	}

	public function admin_menu(): void {
		$icon = 'data:image/svg+xml;base64,';
		add_menu_page(
			__( 'Hostinger', 'hostinger' ),
			__( 'Hostinger', 'hostinger' ),
			'manage_options',
			'hostinger',
			[ $this, 'render' ],
			$icon,
			1
		);

		add_submenu_page(
			'hostinger',
			__( 'Get started', 'hostinger' ),
			__( 'Get started', 'hostinger' ),
			'manage_options',
			'hostinger&#home',
			'__return_empty_string',
			2
		);

		add_submenu_page(
			'hostinger',
			__( 'Learn', 'hostinger' ),
			__( 'Learn', 'hostinger' ),
			'manage_options',
			'hostinger&#learn',
			'__return_empty_string',
			3
		);
		if ( has_action( 'hostinger_ai_assistant_tab_view' ) && current_user_can( 'edit_posts' ) ) {
			add_submenu_page(
				'hostinger',
				__( 'AI assistant', 'hostinger' ),
				__( 'AI assistant', 'hostinger' ),
				'manage_options',
				'hostinger&#ai-assistant',
				'__return_empty_string',
				4
			);
		}

		remove_submenu_page( 'hostinger', 'hostinger' );

	}

	public function render(): void {
		require_once HOSTINGER_ABSPATH . 'includes/admin/onboarding/class-hostinger-onboarding.php';
		$onboarding = new Hostinger_Onboarding();
		include_once __DIR__ . '/views/hostinger-onboarding-view.php';
	}
}

return new Hostinger_Admin_Menu();
